header.script:
SobiCore.Ready( function() {
	function spSwitchPlaceholderType() {
		let selected = SobiCore.El( 'placeholder-type' ).val();
		selected === 'css' ? SobiCore.El( 'placeholder-css' ).removeClass( 'hidden' ) : SobiCore.El( 'placeholder-css' ).addClass( 'hidden' );
		selected === 'picture' ? SobiCore.El( 'placeholder-img' ).removeClass( 'hidden' ) : SobiCore.El( 'placeholder-img' ).addClass( 'hidden' );
	}

	function spShowColWidthEdit() {
		let selected = SobiCore.El( 'col-width-edit' ).val();
		selected === 'twocolumns' ? SobiCore.El( 'label-width-edit' ).removeClass( 'hidden' ) : SobiCore.El( 'label-width-edit' ).addClass( 'hidden' );
	}

	function spShowColWidthSearch() {
		let selected = SobiCore.El( 'col-width-search' ).val();
		selected === 'twocolumns' ? SobiCore.El( 'label-width-search' ).removeClass( 'hidden' ) : SobiCore.El( 'label-width-search' ).addClass( 'hidden' );
	}

	function spShowEfbPositions() {
		let selected = SobiCore.El( 'efb-position' ).attr( 'data-spctrl-active' );
		selected === "1" ? SobiCore.El( 'label-efb-position' ).removeClass( 'hidden' ) : SobiCore.El( 'label-efb-position' ).addClass( 'hidden' );
	}

	function spHideCategories() {
		let selected = SobiCore.El( 'hidecategories' ).val();
		selected === 'none' ? SobiCore.El( 'cat-listing-settings' ).addClass( 'hidden' ) : SobiCore.El( 'cat-listing-settings' ).removeClass( 'hidden' );
	}

	function spShowCarouselSettings() {
		let selected = SobiCore.El( 'showcarousel' ).attr( 'data-spctrl-active' );
		selected === "1" ? SobiCore.El( 'label-showcarousel' ).removeClass( 'hidden' ) : SobiCore.El( 'label-showcarousel' ).addClass( 'hidden' );
	}

	function spShowTabsSettings() {
		let selected = SobiCore.El( 'showtabs' ).attr( 'data-spctrl-active' );
		selected === "1" ? SobiCore.El( 'label-showtabs' ).removeClass( 'hidden' ) : SobiCore.El( 'label-showtabs' ).addClass( 'hidden' );
	}

	function spShowEditTabsSettings() {
		let selected = SobiCore.El( 'showedittabs' ).attr( 'data-spctrl-active' );
		selected === "1" ? SobiCore.El( 'label-showedittabs' ).removeClass( 'hidden' ) : SobiCore.El( 'label-showedittabs' ).addClass( 'hidden' );
	}

	function spShowPrintSettings() {
		let selected = SobiCore.El( 'showprint' ).attr( 'data-spctrl-active' );
		selected === "1" ? SobiCore.El( 'label-showprint' ).removeClass( 'hidden' ) : SobiCore.El( 'label-showprint' ).addClass( 'hidden' );
	}

	function spShowReCaptchaSettings() {
		let selected = SobiCore.El( 'recaptcha' ).attr( 'data-spctrl-active' );
		selected === "1" ? SobiCore.El( 'label-recaptcha' ).removeClass( 'hidden' ) : SobiCore.El( 'label-recaptcha' ).addClass( 'hidden' );
	}

	spSwitchPlaceholderType();
	spShowColWidthEdit();
	spShowColWidthSearch();
	spShowEfbPositions();
	spHideCategories();
	spShowCarouselSettings();
	spShowTabsSettings();
	spShowEditTabsSettings();
	spShowPrintSettings();
	spShowReCaptchaSettings();

	SobiCore.El( 'placeholder-type' ).change( () => {
		spSwitchPlaceholderType();
	} );
	SobiCore.El( 'col-width-edit' ).change( () => {
		spShowColWidthEdit();
	} );
	SobiCore.El( 'col-width-search' ).change( () => {
		spShowColWidthSearch();
	} );
	SobiCore.El( 'efb-position' ).click( () => {
		spShowEfbPositions();
	} );
	SobiCore.El( 'hidecategories' ).change( () => {
		spHideCategories();
	} );
	SobiCore.El( 'showcarousel' ).click( () => {
		spShowCarouselSettings();
	} );
	SobiCore.El( 'showtabs' ).click( () => {
		spShowTabsSettings();
	} );
	SobiCore.El( 'showedittabs' ).click( () => {
		spShowEditTabsSettings();
	} );
	SobiCore.El( 'showprint' ).click( () => {
		spShowPrintSettings();
	} );
	SobiCore.El( 'recaptcha' ).click( () => {
		spShowReCaptchaSettings();
	} );
} );
